        products : res.data
      });


    })


  }
  handleClick= slug => {
    axios.defaults.headers = {
      Authorization: `Bearer ${access}`
    }
    axios.post("http://127.0.0.1:8000/api/addCart/", {slug }).then (res => {
      console.log(res.data);
      this.setState({
        products : res.data
      });


    })

  }

  render() {



    return (
      <section id="Shop">






      <div> 
        <List
    grid={{ gutter: 16, column: 4 }}
    dataSource={this.state.products}
    renderItem={item => (
      <List.Item>
        <Card title={item.title}>

                    <Image 
                    width={200} align = "start"
                  src={item.Product_Image}/> 

     <br/> <br/>

     <Paragraph ellipsis={{ rows: 2, expandable: true, symbol: 'more' }}>
     {item.Description} 
     </Paragraph><br/>
      <div id="priceStyle">{item.price} , {item.slug}</div> 

     <Button type="primary" block onClick={() => this.handleClick(item.slug)

    }> 
     Buy
    </Button>
         </Card>

      </List.Item>
    )}
      />
      </div>
   </section>
    );
  }
}

const mapStateToProps = state => ({
  isAuthenticated: state.auth.isAuthenticated
});

export default connect(mapStateToProps)(ShopProduct);
