import React, { Component, Fragment ,useEffect} from 'react';
import ParticlesBg  from "particles-bg";
import {Link, NavLink , Route } from 'react-router-dom';
import {ImportOutlined ,ShoppingTwoTone,ShoppingCartOutlined } from '@ant-design/icons';
import { checkAuthenticated, load_user ,logout } from '../actions/auth';
import { fetchCart } from '../actions/cart';
import { connect } from 'react-redux';
import {
  Dropdown,

  Header,

  Menu,

} from "semantic-ui-react";


const Nav_bar = ( props, {isAuthenticated , logout,loading}) => {
  useEffect(() => {
    props.checkAuthenticated();
    props.load_user();
    props.fetchCart();
  }, []);




    const geustLinks = () => (
      <Fragment>
       <Link to="/login"> <li><a>login</a></li>
        <li className="smoothscroll"></li></Link>
      </Fragment>
    );

    const authLinks = () => (
      <Link> <li className='nav-item'>
          <a className='nav-link' href='#!' >Logout</a>
      </li></Link>
    );

    console.log(props.cart.id)




    return (

      <header id="home">
      <ParticlesBg type="circle" bg={true} />
      <nav id="nav-wrap">
         <a className="mobile-btn" href="#nav-wrap" title="Show navigation">Show navigation</a>
	      <a className="mobile-btn" href="#home" title="Hide navigation">Hide navigation</a>



         <ul id="nav" className="nav">
           <Link to="/">
            <li className="current"><a>Home</a></li></Link>
            <Link><li><a>Products</a></li></Link>
            <Link><li><a>About</a></li></Link>
            <Link><li><a>Works</a></li></Link>
            <Link to="/contact"><li><a>Contact</a></li></Link>
            <div id="Space">
            {props.isAuthenticated ? authLinks():  geustLinks() }
            <Menu.Menu position='right' id = "icon-nav">
                <Dropdown 
                loading={loading}
                text={`${ props.cart !== null ? props.cart : 0} items`}
                pointing className='link item'>

                  <Dropdown.Menu>
                    <Dropdown.Item>List Item</Dropdown.Item>
                    <Dropdown.Item>List Item</Dropdown.Item>
                    <Dropdown.Divider />
                    <Dropdown.Header>Header Item</Dropdown.Header>
                    <Dropdown.Item>
                      <i className='dropdown icon' />
                      <span className='text'>Submenu</span>
                      <Dropdown.Menu>
                        <Dropdown.Item>List Item</Dropdown.Item>
                        <Dropdown.Item>List Item</Dropdown.Item>
                      </Dropdown.Menu>
                    </Dropdown.Item>
                    <Dropdown.Item>List Item</Dropdown.Item>
                  </Dropdown.Menu>
                </Dropdown>
              </Menu.Menu>    



            </div>


         </ul>

      </nav>



      <p className="scrolldown">
         <a className="smoothscroll" href="#about"><i className="icon-down-circle"></i></a>
      </p>

   </header>
    );


};
const mapStateToProps = state => ({
  isAuthenticated: state.auth.isAuthenticated,
  cart: state.cart.cart,
  loading: state.cart.loading

});
export default connect(mapStateToProps, {checkAuthenticated, load_user , logout,fetchCart})(Nav_bar);
