import React, { useState,Component } from 'react';
import { Redirect, Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { signup,checkAuthenticated } from '../actions/auth';



const Contact = ({ signup, isAuthenticated}) => {
   const [accountCreated, setAccountCreated] = useState(false);



   const [formData, setFormData] = useState({
      email: '',
      first_name: '',
      last_name: '',

      password: '',
      re_password: ''
  });

  const { email, first_name, last_name,  password, re_password } = formData;


  const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = e => {
   e.preventDefault();

   if (password === re_password) {
      signup(email, first_name, last_name,  password, re_password);
      console.log(email, first_name, last_name,  password, re_password)
      setAccountCreated(true);
  }   

};

if (isAuthenticated) {
   return <Redirect to='/' />
}
if (accountCreated) {
   return <Redirect to='/login' />
}
    return (

      <section >
         <div class="loginbody"></div>
            <div class="grad"></div>



            <div class="signup">
                  <form onSubmit={e => onSubmit(e)}>
                  <div>
						   <label>Email <span className="required">*</span></label>
						   <input
                        type='email'
                        placeholder='Email'
                        name='email'
                        value={email}
                        onChange={e => onChange(e)}

                        required
                    />
                  </div>

                  <div>
						   <label>First name <span className="required">*</span></label>
						   <input
                        type='text'
                        placeholder='First Name'
                        name='first_name'
                        value={first_name}
                        onChange={e => onChange(e)}

                        required
                    />
                  </div>
                  <div>
						   <label>Last name <span className="required">*</span></label>
						   <input
                        type='text'
                        placeholder='Last Name'
                        name='last_name'
                        value={last_name}
                        onChange={e => onChange(e)}

                        required
                    />
                  </div>

                  <div>
						   <label>Password <span className="required">*</span></label>
						   <input
                        type='password'
                        placeholder='Password'
                        name='password'
                        value={password}
                        onChange={e => onChange(e)}

                        minLength='6'
                        required
                    />
                  </div>

                  <div>
						   <label>Confirm Password</label>
                     <input
                        type='password'
                        placeholder='Confirm Password'
                        name='re_password'
                        value={re_password}
                        onChange={e => onChange(e)}
                        minLength='6'
                        required
                    />
                  </div>
                  <button type='submit' id='submitbtn'>Register</button>
                  </form>
            </div>




   </section>
    );

};
const mapStateToProps = state => ({
   isAuthenticated: state.auth.isAuthenticated
});

export default connect(mapStateToProps,{signup} )(Contact);
