import React, { useState,Component } from 'react';
import { Redirect, Link } from 'react-router-dom';
import { register } from '../actions/auth';
import { connect } from 'react-redux';


const Contact = ({ register }) => {



   const[formData, setFormData] = useState({
      username : '',
      password: '',
      re_password: ''

   });

   const [accountCreated, setAccountCreated] = useState(false);

   const {username , password , re_password} = formData;

   const onChange = e => setFormData({...formData, [e.target.name]: e.target.value});

   const onSubmit = e => {
      e.preventDefualt();

      if (password == re_password){
         register(username, password, re_password);
         setAccountCreated(true);

      }

   };

   if(accountCreated)
      return < Redirect to='/' />;


    return (
      <section id="contact">

         <div className="row section-head">

            <div className="two columns header-col">

               <h1><span>Sign up.</span></h1>

            </div>

            <div className="ten columns">

                  <p className="lead"></p>

            </div>

         </div>

         <div className="row">
            <div className="eight columns">

               <form action="" method="post" id="contactForm" name="contactForm">
					<fieldset>
                  <form onSubmit={e => onSubmit(e)}>

                  <div>
						   <label htmlFor="contactName">Username <span className="required">*</span></label>
						   <input type="text" defaultValue="" size="35" id="contactName" name="username"
                        onChange = {e=> onChange(e)}
                        value={username}
                      />
                  </div>

                  <div>
						   <label htmlFor="contactEmail">Password <span className="required">*</span></label>
						   <input type="password" defaultValue="" size="35" id="contactEmail" name="password" 
                        onChange = {e=> onChange(e)}
                        value={password}
                        minLength = '6'
                     />
                  </div>

                  <div>
						   <label htmlFor="contactSubject">Confirm Password</label>
                     <input type="password" defaultValue="" size="35" id="contactEmail" name="re_password" 
                        onChange = {e=> onChange(e)}
                        value={re_password}
                        minLength = '6'
                     />
                  </div>



                  <div>
                     <button className="submit" type='submit'>Register</button>
                     <span id="image-loader">
                        <img alt="" src="images/loader.gif" />
                     </span>
                  </div>
                  </form>
					</fieldset>
				   </form>

           <div id="message-warning"> Error boy</div>
				   <div id="message-success">
                  <i className="fa fa-check"></i>Your message was sent, thank you!<br />
				   </div>
           </div>

           <p className='mt-3'>
              Already have an account?! <li><a href="#contact">Sing In.</a></li>
           </p>


            <aside className="four columns footer-widgets">
               <div className="widget widget_contact">

					   <h4>Address and Phone</h4>

				   </div>

               <div className="widget widget_tweets">
                  <h4 className="widget-title">Latest Tweets</h4>
                  <ul id="twitter">
                     <li>
                        <span>
                        This is Photoshop's version  of Lorem Ipsum. Proin gravida nibh vel velit auctor aliquet.
                        Aenean sollicitudin, lorem quis bibendum auctor, nisi elit consequat ipsum
                        <a href="#">http://t.co/CGIrdxIlI3</a>
                        </span>
                        <b><a href="#">2 Days Ago</a></b>
                     </li>
                     <li>
                        <span>
                        Sed ut perspiciatis unde omnis iste natus error sit voluptatem accusantium doloremque laudantium, totam rem aperiam,
                        eaque ipsa quae ab illo inventore veritatis et quasi
                        <a href="#">http://t.co/CGIrdxIlI3</a>
                        </span>
                        <b><a href="#">3 Days Ago</a></b>
                     </li>
                  </ul>
		         </div>
            </aside>
      </div>
   </section>
    );

};

export default connect(null, {register} )(Contact);
