import React from "react";
import axios from 'axios';
import { Button,Header,Container,Table } from 'semantic-ui-react'
import { connect } from "react-redux";
import { Link, Redirect } from "react-router-dom";


class ordersummary extends React.Component {

  state = {
    data: null,
    error: null,
    loading: false
  };

  componentDidMount() {
    this.handleFetchOrder();
  }

  handleFetchOrder = () => {
    this.setState({ loading: true });
    const token = localStorage.getItem('access');
    const config = {
      headers: {
        'Authorization': 'JWT ' + token
      }
    };
       axios.get(`http://127.0.0.1:8000/api/fetchCart/` ,config)
      .then(res => {
        this.setState({ data: res.data, loading: false });
      })
      .catch(err => {
       
          this.setState({
            error: "You currently do not have an order",
            loading: false
          });
        
        
      });
  };
  
  render() {
    const { data, error, loading } = this.state;
    console.log(data);
   
    

    return (
    <Container> 
      <br/>
      <Header>Order Summary</Header>
   

        {data &&<Table celled inverted selectable>
          <Table.Header>
            <Table.Row>
              <Table.HeaderCell>Order number</Table.HeaderCell>
              <Table.HeaderCell>Name</Table.HeaderCell>
              <Table.HeaderCell>quantity</Table.HeaderCell>
              <Table.HeaderCell>price</Table.HeaderCell>
              <Table.HeaderCell>Gross Total</Table.HeaderCell>
            </Table.Row>
          </Table.Header>

          <Table.Body>
            {data.map((order_item, i) =>{
              return(
                <Table.Row key= {order_item.id}>
                  <Table.Cell>{i}</Table.Cell>
                  <Table.Cell>{order_item.item}</Table.Cell>
                  <Table.Cell textAlign='right'>{order_item.quantity}</Table.Cell>
                  <Table.Cell textAlign='right'>{order_item.quantity}</Table.Cell>
                  <Table.Cell textAlign='right'>{order_item.quantity}</Table.Cell>
              </Table.Row>
              )
            })}
           
            

            <Table.Row>
                <Table.Cell></Table.Cell>
                <Table.Cell></Table.Cell>
                <Table.Cell textAlign='right'>
                  <Button color = 'black'>Checkout</Button>
                </Table.Cell>
              </Table.Row>
          </Table.Body>
        </Table>}
        <br/>
      </Container>
      
      
    )
  }
}



export default ordersummary;
