import { CART_START, CART_SUCCESS, CART_FAIL } from '../actions/actionsTypes';

const initialState = {
  scart: null,
  error: null,
  datalen : 0,
  loading: false
};

const cartStart = (state, action) => {
  return (state, {
    error: null,
    loading: true
  });
};

const cartSuccess = (state, action) => {
  return(state, {
    scart: action.data,
    datalen: action.data.length,
    error: null,
    loading: false
  });
 
};

const cartFail = (state, action) => {
  return updateObject(state, {
    error: action.error,
    loading: false
  });
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case CART_START:
      return cartStart(state, action);
    case CART_SUCCESS:
      return cartSuccess(state, action);
    case CART_FAIL:
      return cartFail(state, action);
    default:
      return state;
  }
};

export default reducer;
