import React, { Component, Fragment ,useEffect} from 'react';
import ParticlesBg  from "particles-bg";
import {Link, NavLink , Route } from 'react-router-dom';
import {ImportOutlined ,ShoppingTwoTone,ShoppingCartOutlined } from '@ant-design/icons';
import { checkAuthenticated, load_user ,logout } from '../actions/auth';
import { fetchCart } from '../actions/cart';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import slider1 from '../images/slider1.jpg';
import slider2 from '../images/slider2.jpg';
import slider3 from '../images/slider3.jpg';
import slider4 from '../images/slider4.jpg';

import {
  Dropdown,

  Header,

  Menu,

} from "semantic-ui-react";

const Nav_bar = ( props, {}) => {
  useEffect(() => {
    props.checkAuthenticated();
    props.load_user();
    props.fetchCart();
  }, []);




    const geustLinks = () => (
      <Fragment>
       <Link to="/login"> <li><a>login</a></li>
        </Link>
      </Fragment>
    );

    const authLinks = () => (
      <Fragment>
      <Link> <li>
          <a href='#!'>logout</a>
      </li></Link>
      </Fragment>
    );

    console.log(props.cart)





    return (
      <header id="home">


        <div class="slideshow-container">

            <div class="mySlides fade">
              <div class="numbertext">1 / 3</div>
              <img src={slider1}></img>
              <div class="text">Caption Text</div>
            </div>

            <div class="mySlides fade">
              <div class="numbertext">2 / 3</div>
              <img src={slider2} ></img>
              <div class="text">Caption Two</div>
            </div>

            <div class="mySlides fade">
              <div class="numbertext">3 / 3</div>
              <img src='../images/slider1.jpg' ></img>
              <div class="text">Caption Three</div>
            </div>

            </div>
            <br/>

              <div>
                <span class="dot"></span> 
                <span class="dot"></span> 
                <span class="dot"></span> 
              </div>


      <nav id="nav-wrap">
         <a className="mobile-btn" href="#nav-wrap" title="Show navigation">Show navigation</a>
	      <a className="mobile-btn" href="#home" title="Hide navigation">Hide navigation</a>



         <ul id="nav" className="nav">
           <Link to="/">
            <li className="current"><a>Home</a></li></Link>
            <Link><li><a>Products</a></li></Link>
            <Link><li><a>About</a></li></Link>

            <div id="left">

            {props.isAuthenticated ? authLinks():  geustLinks() }

            </div>

         </ul>
         <div id ="right">
            <Menu.Menu position='right' id = "icon-nav">
                <Dropdown 
                icon='cart'
                text={`${ props.cart !== null ? props.cart.length : 0} `}

                pointing className='link item'>

               <Dropdown.Menu>
                    {/* {props.cart && JSON.parse(props.cart).map(item => {
                    <Dropdown.Item key={order_item.id}> {item.quantity} x {item.item}</Dropdown.Item>
                  })}   */}
                  <Dropdown.Item> {props.cart !== null ? props.cart[0].quantity : 0} x {props.cart !== null ? props.cart[0].item : 'No item'}</Dropdown.Item>
                  {props.cart && props.cart == null ? (<Dropdown.Item> No items</Dropdown.Item>) : null }

                    <Dropdown.Divider />
                    <Dropdown.Item text='CheckOut' icon='arrow right' onClick={ () => props.history.push('/checkout')}/>
                  </Dropdown.Menu>
                </Dropdown>
              </Menu.Menu>  
            </div>

      </nav>



      <p className="scrolldown">
         <a className="smoothscroll" href="#about"><i className="icon-down-circle"></i></a>
      </p>

   </header>
    );


};
const mapStateToProps = state => ({
  isAuthenticated: state.auth.isAuthenticated,
  cart: state.cart.cart,
  loading: state.cart.loading

});
export default withRouter (connect(mapStateToProps, {checkAuthenticated, load_user , logout,fetchCart})(Nav_bar));
